'use strict';

var ShortMessage = require('../../models/ShortMessage');
var clClient = require('./clClient');

var smManager = function(){
	return{
		send: function(shortMessage, callback){

			var msg = clClient.send(shortMessage,function(err){
				shortMessage.updatedAt = new Date();
				callback(err);
			});

		}
	};

};

module.exports = smManager();